INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1457, '強化を掴む　完全な調和と磨くこと', '進化への道', '波長と完成について。運命の人に関する深い結びつきを、ハワイアンタロットが徹底解明。', 5, NULL, NULL, NULL, NULL, NULL, '2027-02-20 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1457, 145701, '心を許す人が求める完全な調和'),
(1457, 145702, '結実における心身の調和'),
(1457, 145703, '相手の親密度と昇華'),
(1457, 145704, '調和の意味と充実'),
(1457, 145705, '大切な人が望む一体感'),
(1457, 145706, '愛の極致を左右する要因'),
(1457, 145707, '理解し合える人の心地よさと進化'),
(1457, 145708, '一体となる人との進化');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1457, 145701, 14570101, 'タロットが告げるのは【I’O】。ミステリーというキーワードで、全ては必然で起こっていることを伝えています。心を許す人が求める完全な調和において最も大切なのは、ミステリーという概念を正しく認識することです。ミステリーは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてミステリーと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、ミステリーと共に成長していきましょう。未来は明るく輝いています。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145701, 14570102, '【LAMAKU】のカードが語りかけます。意思の力で喜びを手にするという智恵を、あなたに授けようとしています。心を許す人が求める完全な調和において、勝利が極めて重要な役割を果たしています。この勝利のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。勝利という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145701, 14570103, '【PUA】のカードが語りかけます。目的を定め前進あるのみという智恵を、あなたに授けようとしています。タロットが告げる心を許す人が求める完全な調和に関する真実は、前進に集約されています。前進という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。前進を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1457, 145701, 14570104, '【ALOHA】のカードが語りかけます。真の愛を学ぶという智恵を、あなたに授けようとしています。タロットが告げる心を許す人が求める完全な調和に関する真実は、愛に集約されています。愛という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。愛を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145702, 14570201, '【MENEHUNE】のカードが現れました。持続を象徴し、コツコツと積み重ねることを教えてくれます。結実における心身の調和に関して、カードは持続という重要なメッセージを伝えています。あなたが今必要としているのは、この持続を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。持続の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1457, 145702, 14570202, 'ハワイアンタロットの【MAUI】。トリックスターのエネルギーを持ち、失敗を恐れずチャレンジすることを説いています。結実における心身の調和に関して、カードはトリックスターという重要なメッセージを伝えています。あなたが今必要としているのは、このトリックスターを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。トリックスターの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145702, 14570203, 'ハワイアンタロットの【PO/AO】。無意識/意識のエネルギーを持ち、深層心理を探ることを説いています。結実における心身の調和に関して、カードは無意識/意識という重要なメッセージを伝えています。あなたが今必要としているのは、この無意識/意識を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。無意識/意識の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1457, 145702, 14570204, '【MAMALAHOE】のカードが出ました。このカードは「自分の気持ちを最優先する」を意味します。結実における心身の調和に関して、カードは同情という重要なメッセージを伝えています。あなたが今必要としているのは、この同情を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。同情の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145703, 14570301, '【TI】のカードが出ました。このカードは「感情、肉体、精神の浄化をする」を意味します。相手の親密度と昇華において最も大切なのは、浄化という概念を正しく認識することです。浄化は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて浄化と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、浄化と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145703, 14570302, '【KANALOA】のカードが語りかけます。基盤づくりをするという智恵を、あなたに授けようとしています。相手の親密度と昇華において最も大切なのは、基盤という概念を正しく認識することです。基盤は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて基盤と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、基盤と共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1457, 145703, 14570303, 'ハワイアンタロットの【OLE】。物語のエネルギーを持ち、言葉を大切にすることを説いています。カードが示す相手の親密度と昇華の答えは、物語にあります。この物語を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、物語という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、物語の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145703, 14570304, '出たカードは【ANO’ANO】。思考が現実を創る／全てを受けとめることを示す重要なカードです。あなたの相手の親密度と昇華には、意思が深く関わっています。この意思というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。意思のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、意思と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145704, 14570401, 'カードに現れたのは【POHAKU】。テーマは「人間関係を見直す」です。カードが示す調和の意味と充実の答えは、関係にあります。この関係を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、関係という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、関係の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145704, 14570402, '出たカードは【MO’O】。不安が成功を妨げることを示す重要なカードです。調和の意味と充実に対するハワイアンタロットの答えは明確です。それは罠です。この罠という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。罠は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で罠を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145704, 14570403, '【LA’AMAOMAO】のカードが出ました。このカードは「失敗は成功の学び」を意味します。あなたが知りたい調和の意味と充実の核心は、償いにあります。償いというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。償いを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1457, 145704, 14570404, '【IWI】のカードが語りかけます。自分も他人も大切にするという智恵を、あなたに授けようとしています。カードが明かす調和の意味と充実の真実において、尊敬は中心的な役割を果たしています。尊敬を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。尊敬のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、尊敬という贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145705, 14570501, 'カードに現れたのは【HO’OPONOPONO】。テーマは「コミュニケーションを大切にする」です。大切な人が望む一体感に対するハワイアンタロットの答えは明確です。それはコミュニケーションです。このコミュニケーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。コミュニケーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でコミュニケーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1457, 145705, 14570502, '【KANE】のカードが出ました。このカードは「もう一度始める」を意味します。タロットが告げる大切な人が望む一体感に関する真実は、創造に集約されています。創造という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。創造を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1457, 145705, 14570503, '出たカードは【PUHI】。新しい創造のための破壊ことを示す重要なカードです。大切な人が望む一体感において、崩壊が極めて重要な役割を果たしています。この崩壊のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。崩壊という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1457, 145705, 14570504, '【KA WAI A KA PILILIKO】が選ばれました。「ありのままを受け入れる」がこのカードの核心的メッセージです。大切な人が望む一体感を考える上で、反映は欠かせない要素となっています。反映の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、反映の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、反映のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1457, 145706, 14570601, '【PUEO】が選ばれました。「自然の流れに身をまかせる」がこのカードの核心的メッセージです。愛の極致を左右する要因において、導きが極めて重要な役割を果たしています。この導きのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。導きという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145706, 14570602, '出たカードは【HINA】。自分自身を成長させることを示す重要なカードです。タロットが告げる愛の極致を左右する要因に関する真実は、養いに集約されています。養いという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。養いを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1457, 145706, 14570603, '【LAKA】のカードが出ました。このカードは「直感を大切にする」を意味します。カードが示す愛の極致を左右する要因の答えは、インスピレーションにあります。このインスピレーションを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、インスピレーションという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、インスピレーションの導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1457, 145706, 14570604, '【PAPA&WAKEA】のカードが示されました。「葛藤の中から新しいものを生み出す」というメッセージが込められています。愛の極致を左右する要因に関して、カードは苦闘・全体という重要なメッセージを伝えています。あなたが今必要としているのは、この苦闘・全体を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。苦闘・全体の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145707, 14570701, 'タロットが告げるのは【NAUPAKA】。別離というキーワードで、切り離すことを伝えています。カードが示す理解し合える人の心地よさと進化の答えは、別離にあります。この別離を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、別離という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、別離の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145707, 14570702, '【PU’OLO】のカードが出ました。このカードは「不必要なものを手放す」を意味します。カードが示す理解し合える人の心地よさと進化の答えは、解放にあります。この解放を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、解放という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、解放の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145707, 14570703, '出たカードは【HUNA】。可能性を禁じないことを示す重要なカードです。理解し合える人の心地よさと進化において、秘密が極めて重要な役割を果たしています。この秘密のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。秘密という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1457, 145707, 14570704, '【KU/HINA】のカードが現れました。戦い/癒しを象徴し、活動する/休息することを教えてくれます。カードが示す理解し合える人の心地よさと進化の答えは、戦い/癒しにあります。この戦い/癒しを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、戦い/癒しという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、戦い/癒しの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145708, 14570801, '【LONO/KAMAPUA’A】のカードが示されました。「やり切ることで収穫に繋がる」というメッセージが込められています。一体となる人との進化において、収穫/豊穣が極めて重要な役割を果たしています。この収穫/豊穣のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。収穫/豊穣という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1457, 145708, 14570802, '【MANA】のカードが現れました。力を与えることを象徴し、満ちているエネルギーをどう使うかことを教えてくれます。カードが示す一体となる人との進化の答えは、力を与えることにあります。この力を与えることを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、力を与えることという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、力を与えることの導きに従ってください。答えは必ずそこにあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145708, 14570803, '出たカードは【LEHUA】。安心して前進することを示す重要なカードです。カードが明かす一体となる人との進化の真実において、イニシエーションは中心的な役割を果たしています。イニシエーションを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。イニシエーションのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、イニシエーションという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1457, 145708, 14570804, 'タロットが告げるのは【HAUMEA】。滋養というキーワードで、必要なものは与えられることを伝えています。一体となる人との進化を考える上で、滋養は欠かせない要素となっています。滋養の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、滋養の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、滋養のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
