INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1465, '大切な人との絆　先行きの予測を占う感情の核心', '誠実な感情の全て', 'あの人との心情の解明。正直な心情や先の見通しについて、ハワイアンタロットが詳しく教えます。', 3, NULL, NULL, NULL, NULL, NULL, '2027-02-28 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1465, 146501, '今後の流れを実現する相応しい態度'),
(1465, 146502, '将来の姿を実現する推奨される行動'),
(1465, 146503, '先の展望の展望'),
(1465, 146504, '心の真実から見える真実'),
(1465, 146505, '偽らざる思いにおける心の真相'),
(1465, 146506, '先々の見通しについて');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1465, 146501, 14650101, '出たカードは【PU’OLO】。不必要なものを手放すことを示す重要なカードです。今後の流れを実現する相応しい態度において最も大切なのは、解放という概念を正しく認識することです。解放は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて解放と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、解放と共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1465, 146501, 14650102, 'カードに現れたのは【MAUI】。テーマは「失敗を恐れずチャレンジする」です。今後の流れを実現する相応しい態度において、トリックスターが極めて重要な役割を果たしています。このトリックスターのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。トリックスターという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1465, 146501, 14650103, '【LEI】のカードが示されました。「日々の暮らしを大切に」というメッセージが込められています。今後の流れを実現する相応しい態度に対するハワイアンタロットの答えは明確です。それは大切です。この大切という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。大切は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で大切を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1465, 146501, 14650104, 'ハワイアンタロットの【KU/HINA】。戦い/癒しのエネルギーを持ち、活動する/休息することを説いています。タロットが告げる今後の流れを実現する相応しい態度に関する真実は、戦い/癒しに集約されています。戦い/癒しという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。戦い/癒しを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1465, 146502, 14650201, '【KANE】が選ばれました。「もう一度始める」がこのカードの核心的メッセージです。将来の姿を実現する推奨される行動において最も大切なのは、創造という概念を正しく認識することです。創造は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて創造と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、創造と共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1465, 146502, 14650202, '【KANALOA】のカードが示されました。「基盤づくりをする」というメッセージが込められています。将来の姿を実現する推奨される行動において、基盤が極めて重要な役割を果たしています。この基盤のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。基盤という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1465, 146502, 14650203, '【MANO】が選ばれました。「リーダーシップを取る」がこのカードの核心的メッセージです。あなたが知りたい将来の姿を実現する推奨される行動の核心は、リーダーシップにあります。リーダーシップというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。リーダーシップを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1465, 146502, 14650204, '【POHAKU】のカードが出ました。このカードは「人間関係を見直す」を意味します。あなたが知りたい将来の姿を実現する推奨される行動の核心は、関係にあります。関係というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。関係を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1465, 146503, 14650301, 'ハワイアンタロットの【KA WAI A KA PILILIKO】。反映のエネルギーを持ち、ありのままを受け入れることを説いています。あなたの先の展望の展望には、反映が深く関わっています。この反映というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。反映のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、反映と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1465, 146503, 14650302, '【NAUPAKA】が選ばれました。「切り離す」がこのカードの核心的メッセージです。カードが明かす先の展望の展望の真実において、別離は中心的な役割を果たしています。別離を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。別離のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、別離という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146503, 14650303, '出たカードは【LONO/KAMAPUA’A】。やり切ることで収穫に繋がることを示す重要なカードです。カードが明かす先の展望の展望の真実において、収穫/豊穣は中心的な役割を果たしています。収穫/豊穣を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。収穫/豊穣のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、収穫/豊穣という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1465, 146503, 14650304, 'ハワイアンタロットの【PAPA&WAKEA】。苦闘・全体のエネルギーを持ち、葛藤の中から新しいものを生み出すことを説いています。先の展望の展望において、苦闘・全体が極めて重要な役割を果たしています。この苦闘・全体のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。苦闘・全体という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1465, 146504, 14650401, '【IWI】のカードが示されました。「自分も他人も大切にする」というメッセージが込められています。あなたが知りたい心の真実から見える真実の核心は、尊敬にあります。尊敬というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。尊敬を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1465, 146504, 14650402, 'カードに現れたのは【TAPA】。テーマは「パートナーシップ、距離感を考える」です。心の真実から見える真実において、結合が極めて重要な役割を果たしています。この結合のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。結合という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146504, 14650403, '【HO’OPONOPONO】のカードです。HO’OPONOPONOが示すのはコミュニケーションであり、コミュニケーションを大切にすることの重要性です。カードが明かす心の真実から見える真実の真実において、コミュニケーションは中心的な役割を果たしています。コミュニケーションを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。コミュニケーションのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、コミュニケーションという贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146504, 14650404, 'カードに現れたのは【HI’IAKA】。テーマは「望む結果をイメージする」です。心の真実から見える真実に対するハワイアンタロットの答えは明確です。それは献身です。この献身という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。献身は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で献身を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1465, 146505, 14650501, 'カードに現れたのは【NALU】。テーマは「感情を観察する」です。あなたの偽らざる思いにおける心の真相には、黙想が深く関わっています。この黙想というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。黙想のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、黙想と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1465, 146505, 14650502, '出たカードは【PELE】。情熱を注ぐことを示す重要なカードです。あなたが知りたい偽らざる思いにおける心の真相の核心は、隆起にあります。隆起というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。隆起を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146505, 14650503, '【AWA】のカードが語りかけます。無条件と見返りの違いを意識するという智恵を、あなたに授けようとしています。偽らざる思いにおける心の真相を考える上で、供物・生贄は欠かせない要素となっています。供物・生贄の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、供物・生贄の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、供物・生贄のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1465, 146505, 14650504, 'タロットが告げるのは【HUNA】。秘密というキーワードで、可能性を禁じないことを伝えています。偽らざる思いにおける心の真相に対するハワイアンタロットの答えは明確です。それは秘密です。この秘密という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。秘密は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で秘密を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146506, 14650601, '【PO/AO】が選ばれました。「深層心理を探る」がこのカードの核心的メッセージです。先々の見通しについてに関して、カードは無意識/意識という重要なメッセージを伝えています。あなたが今必要としているのは、この無意識/意識を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。無意識/意識の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1465, 146506, 14650602, '【HULU】が選ばれました。「無限の可能性」がこのカードの核心的メッセージです。先々の見通しについてにおいて最も大切なのは、イリュージョンという概念を正しく認識することです。イリュージョンは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイリュージョンと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イリュージョンと共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1465, 146506, 14650603, '【I’O】が選ばれました。「全ては必然で起こっている」がこのカードの核心的メッセージです。タロットが告げる先々の見通しについてに関する真実は、ミステリーに集約されています。ミステリーという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。ミステリーを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1465, 146506, 14650604, '【PUEO】のカードが示されました。「自然の流れに身をまかせる」というメッセージが込められています。先々の見通しについてに対するハワイアンタロットの答えは明確です。それは導きです。この導きという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。導きは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で導きを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。');
