INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1472, '恋愛バイオリズムを知る　出会いを待つあなたとの縁の成立予想', '幸せを掴みたいあなたを占う', '出会いの引力と運命的瞬間について。恋を求めるあなたに関する恋愛の予感を、ハワイアンタロットが徹底解明。', 10, NULL, NULL, NULL, NULL, NULL, '2027-03-07 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1472, 147201, '恋愛傾向の意味と出会いの実現'),
(1472, 147202, '特別な縁の行方'),
(1472, 147203, '新しい恋を実現するポジティブさ'),
(1472, 147204, '自分自身の恋愛エネルギー'),
(1472, 147205, '出会いの運命の行方'),
(1472, 147206, '出会いの幸運の行方');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1472, 147201, 14720101, '【HINA】のカードが示されました。「自分自身を成長させる」というメッセージが込められています。恋愛傾向の意味と出会いの実現において、養いが極めて重要な役割を果たしています。この養いのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。養いという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1472, 147201, 14720102, '【KU/HINA】のカードが現れました。戦い/癒しを象徴し、活動する/休息することを教えてくれます。恋愛傾向の意味と出会いの実現において、戦い/癒しが極めて重要な役割を果たしています。この戦い/癒しのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。戦い/癒しという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147201, 14720103, '【LAMAKU】のカードです。LAMAKUが示すのは勝利であり、意思の力で喜びを手にすることの重要性です。カードが示す恋愛傾向の意味と出会いの実現の答えは、勝利にあります。この勝利を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、勝利という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、勝利の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1472, 147201, 14720104, '【HULU】が選ばれました。「無限の可能性」がこのカードの核心的メッセージです。カードが示す恋愛傾向の意味と出会いの実現の答えは、イリュージョンにあります。このイリュージョンを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、イリュージョンという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、イリュージョンの導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147202, 14720201, 'ハワイアンタロットの【PU’OLO】。解放のエネルギーを持ち、不必要なものを手放すことを説いています。カードが示す特別な縁の行方の答えは、解放にあります。この解放を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、解放という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、解放の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147202, 14720202, '【TI】のカードです。TIが示すのは浄化であり、感情、肉体、精神の浄化をすることの重要性です。あなたが知りたい特別な縁の行方の核心は、浄化にあります。浄化というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。浄化を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1472, 147202, 14720203, 'ハワイアンタロットの【KA WAI A KA PILILIKO】。反映のエネルギーを持ち、ありのままを受け入れることを説いています。あなたが知りたい特別な縁の行方の核心は、反映にあります。反映というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。反映を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1472, 147202, 14720204, 'タロットが告げるのは【PAHU】。タイミング・儀式というキーワードで、ターニングポイントことを伝えています。カードが明かす特別な縁の行方の真実において、タイミング・儀式は中心的な役割を果たしています。タイミング・儀式を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。タイミング・儀式のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、タイミング・儀式という贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1472, 147203, 14720301, '【HAUMEA】が選ばれました。「必要なものは与えられる」がこのカードの核心的メッセージです。新しい恋を実現するポジティブさに関して、カードは滋養という重要なメッセージを伝えています。あなたが今必要としているのは、この滋養を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。滋養の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1472, 147203, 14720302, '【PUA】のカードが出ました。このカードは「目的を定め前進あるのみ」を意味します。カードが明かす新しい恋を実現するポジティブさの真実において、前進は中心的な役割を果たしています。前進を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。前進のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、前進という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147203, 14720303, '【LONO/KAMAPUA’A】のカードが示されました。「やり切ることで収穫に繋がる」というメッセージが込められています。新しい恋を実現するポジティブさにおいて、収穫/豊穣が極めて重要な役割を果たしています。この収穫/豊穣のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。収穫/豊穣という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147203, 14720304, '出たカードは【OLE】。言葉を大切にすることを示す重要なカードです。カードが明かす新しい恋を実現するポジティブさの真実において、物語は中心的な役割を果たしています。物語を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。物語のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、物語という贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1472, 147204, 14720401, '【MO’O】のカードが示されました。「不安が成功を妨げる」というメッセージが込められています。自分自身の恋愛エネルギーを考える上で、罠は欠かせない要素となっています。罠の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、罠の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、罠のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147204, 14720402, '【MANO】のカードが示されました。「リーダーシップを取る」というメッセージが込められています。自分自身の恋愛エネルギーにおいて最も大切なのは、リーダーシップという概念を正しく認識することです。リーダーシップは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてリーダーシップと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、リーダーシップと共に成長していきましょう。未来は明るく輝いています。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1472, 147204, 14720403, '【LA’AMAOMAO】のカードが示されました。「失敗は成功の学び」というメッセージが込められています。自分自身の恋愛エネルギーに対するハワイアンタロットの答えは明確です。それは償いです。この償いという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。償いは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で償いを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147204, 14720404, '【MENEHUNE】のカードが出ました。このカードは「コツコツと積み重ねる」を意味します。タロットが告げる自分自身の恋愛エネルギーに関する真実は、持続に集約されています。持続という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。持続を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147205, 14720501, '【NALU】のカードが語りかけます。感情を観察するという智恵を、あなたに授けようとしています。あなたの出会いの運命の行方には、黙想が深く関わっています。この黙想というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。黙想のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、黙想と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147205, 14720502, '【MANA】のカードが語りかけます。満ちているエネルギーをどう使うかという智恵を、あなたに授けようとしています。出会いの運命の行方を考える上で、力を与えることは欠かせない要素となっています。力を与えることの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、力を与えることの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、力を与えることのメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147205, 14720503, 'カードに現れたのは【NAUPAKA】。テーマは「切り離す」です。あなたの出会いの運命の行方には、別離が深く関わっています。この別離というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。別離のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、別離と共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147205, 14720504, 'ハワイアンタロットの【ANUENUE】。変容のエネルギーを持ち、隠れた部分に光をあてることを説いています。あなたが知りたい出会いの運命の行方の核心は、変容にあります。変容というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。変容を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147206, 14720601, '【MAUI】のカードが出ました。このカードは「失敗を恐れずチャレンジする」を意味します。タロットが告げる出会いの幸運の行方に関する真実は、トリックスターに集約されています。トリックスターという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。トリックスターを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1472, 147206, 14720602, 'タロットが告げるのは【KANALOA】。基盤というキーワードで、基盤づくりをすることを伝えています。出会いの幸運の行方において、基盤が極めて重要な役割を果たしています。この基盤のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。基盤という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1472, 147206, 14720603, '【IWI】のカードが語りかけます。自分も他人も大切にするという智恵を、あなたに授けようとしています。タロットが告げる出会いの幸運の行方に関する真実は、尊敬に集約されています。尊敬という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。尊敬を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1472, 147206, 14720604, '【ANO’ANO】のカードです。ANO’ANOが示すのは意思であり、思考が現実を創る／全てを受けとめることの重要性です。タロットが告げる出会いの幸運の行方に関する真実は、意思に集約されています。意思という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。意思を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。');
