INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1485, '偽りない感情が知りたい！　思う人との未来の動向', '先行きの予測予測', '気になる人との真意の把握。本音の部分や先行きの予測について、ハワイアンタロットが詳しく教えます。', 3, NULL, NULL, NULL, NULL, NULL, '2027-03-20 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1485, 148501, '感情の真相の核心'),
(1485, 148502, '見守る相手との今後の成り行き'),
(1485, 148503, '真摯な思いの本質'),
