INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1490, '恋愛ブロックの真実　大切な人との理想の相手は', '大切な人を占う', '大切な人との新しい恋が明らかに。恋愛ブロックと理想の相手を、ハワイアンタロットが徹底的に占います。', 10, NULL, NULL, NULL, NULL, NULL, '2027-03-25 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1490, 149001, '現在の恋愛ブロック'),
(1490, 149002, '理想の相手への道'),
(1490, 149003, '大切な人との新しい恋'),
(1490, 149004, '心がける魅力の高め方'),
(1490, 149005, '新しい恋についての詳細'),
(1490, 149006, '大切な人の隠れた面'),
(1490, 149007, '理想の相手を左右する要因'),
(1490, 149008, '改善のための魅力の高め方'),
(1490, 149009, '恋愛ブロックの深層');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1490, 149001, 14900101, '【LAKA】のカードが示されました。「直感を大切にする」というメッセージが込められています。現在の恋愛ブロックについて、インスピレーションという視点から捉えることが非常に重要です。インスピレーションのエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149001, 14900102, '出たカードは【MANA】。満ちているエネルギーをどう使うかことを示す重要なカードです。現在の恋愛ブロックについて、力を与えることという視点から捉えることが非常に重要です。力を与えることのエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149001, 14900103, '【HINA】のカードが出ました。このカードは「自分自身を成長させる」を意味します。現在の恋愛ブロックについて、養いという視点から捉えることが非常に重要です。養いのエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149001, 14900104, '【TAPA】のカードが示されました。「パートナーシップ、距離感を考える」というメッセージが込められています。現在の恋愛ブロックについて、結合という視点から捉えることが非常に重要です。結合のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149002, 14900201, '【PAPA&WAKEA】のカードが出ました。カードのテーマは「葛藤の中から新しいものを生み出す」です。理想の相手への道を考える上で、苦闘・全体が中心的なキーワードとなります。苦闘・全体の本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149002, 14900202, '【HI’IAKA】のカードが出ました。HI’IAKAは献身を象徴し、望む結果をイメージすることを教えてくれます。理想の相手への道を考える上で、献身が中心的なキーワードとなります。献身の本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149002, 14900203, '【MANO】のカードが示されました。「リーダーシップを取る」というメッセージが込められています。理想の相手への道を考える上で、リーダーシップが中心的なキーワードとなります。リーダーシップの本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149002, 14900204, '【LEI】のカードが出ました。カードのテーマは「日々の暮らしを大切に」です。理想の相手への道を考える上で、大切が中心的なキーワードとなります。大切の本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149003, 14900301, '【HO’OPONOPONO】のカードが出ました。HO’OPONOPONOはコミュニケーションを象徴し、コミュニケーションを大切にすることを教えてくれます。大切な人との新しい恋において、コミュニケーションがキーワードとなります。コミュニケーションの気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149003, 14900302, '【ANUENUE】のカードが示されました。「隠れた部分に光をあてる」というメッセージが込められています。大切な人との新しい恋において、変容がキーワードとなります。変容の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149003, 14900303, '【PELE】のカードが示されました。「情熱を注ぐ」というメッセージが込められています。大切な人との新しい恋において、隆起がキーワードとなります。隆起の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149003, 14900304, '【MAMALAHOE】のカードが示されました。「自分の気持ちを最優先する」というメッセージが込められています。大切な人との新しい恋において、同情がキーワードとなります。同情の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149004, 14900401, '出たカードは【TAPA】。パートナーシップ、距離感を考えることを示す重要なカードです。心がける魅力の高め方において、結合がキーワードとなります。結合の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149004, 14900402, '【ANO’ANO】のカードが出ました。ANO’ANOは意思を象徴し、思考が現実を創る／全てを受けとめることを教えてくれます。心がける魅力の高め方において、意思がキーワードとなります。意思の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149004, 14900403, '【HI’IAKA】のカードが出ました。カードのテーマは「望む結果をイメージする」です。心がける魅力の高め方において、献身がキーワードとなります。献身の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149004, 14900404, '【PUA】のカードが出ました。PUAは前進を象徴し、目的を定め前進あるのみことを教えてくれます。心がける魅力の高め方において、前進がキーワードとなります。前進の気持ちを常に持ち続け、それを行動に移していくことで、望む結果へと着実に近づいていけるでしょう。焦る必要はありません。一つひとつ丁寧に積み重ねていくことが、最終的な成功につながります。周囲の声に惑わされず、自分の道を信じて歩んでください。あなたの努力は必ず報われます。'),
(1490, 149005, 14900501, '【MANA】のカードが出ました。このカードは「満ちているエネルギーをどう使うか」を意味します。新しい恋についての詳細を考える上で、力を与えることが中心的なキーワードとなります。力を与えることの本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149005, 14900502, '【LAKA】のカードが出ました。LAKAはインスピレーションを象徴し、直感を大切にすることを教えてくれます。新しい恋についての詳細を考える上で、インスピレーションが中心的なキーワードとなります。インスピレーションの本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149005, 14900503, '【LAMAKU】のカードが示されました。「意思の力で喜びを手にする」というメッセージが込められています。新しい恋についての詳細を考える上で、勝利が中心的なキーワードとなります。勝利の本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149005, 14900504, '【KANE】のカードが出ました。カードのテーマは「もう一度始める」です。新しい恋についての詳細を考える上で、創造が中心的なキーワードとなります。創造の本質をしっかりと見つめ、その意味を深く理解することで、あなたが求めている答えが明確に見えてくるはずです。表面的な理解だけでなく、本質的な部分まで掘り下げる姿勢が大切です。時間をかけて熟考し、真実を見極めてください。必ず光が見えてきます。'),
(1490, 149006, 14900601, '【NALU】のカードが出ました。このカードは「感情を観察する」を意味します。大切な人の隠れた面として、黙想を強く意識することが非常に大切です。日々の生活の中で黙想を心がけ、実践し続けることで、道は自然と開けていきます。小さな一歩の積み重ねが、やがて大きな成果となって現れます。自分を信じ、諦めずに前進し続けてください。困難に直面しても、黙想の精神を忘れなければ、必ず乗り越えられます。あなたには無限の可能性があります。'),
(1490, 149006, 14900602, '出たカードは【ALOHA】。真の愛を学ぶことを示す重要なカードです。大切な人の隠れた面として、愛を強く意識することが非常に大切です。日々の生活の中で愛を心がけ、実践し続けることで、道は自然と開けていきます。小さな一歩の積み重ねが、やがて大きな成果となって現れます。自分を信じ、諦めずに前進し続けてください。困難に直面しても、愛の精神を忘れなければ、必ず乗り越えられます。あなたには無限の可能性があります。'),
(1490, 149006, 14900603, '【PAPA&WAKEA】のカードが示されました。「葛藤の中から新しいものを生み出す」というメッセージが込められています。大切な人の隠れた面として、苦闘・全体を強く意識することが非常に大切です。日々の生活の中で苦闘・全体を心がけ、実践し続けることで、道は自然と開けていきます。小さな一歩の積み重ねが、やがて大きな成果となって現れます。自分を信じ、諦めずに前進し続けてください。困難に直面しても、苦闘・全体の精神を忘れなければ、必ず乗り越えられます。あなたには無限の可能性があります。'),
(1490, 149006, 14900604, '出たカードは【ALOHA】。真の愛を学ぶことを示す重要なカードです。大切な人の隠れた面として、愛を強く意識することが非常に大切です。日々の生活の中で愛を心がけ、実践し続けることで、道は自然と開けていきます。小さな一歩の積み重ねが、やがて大きな成果となって現れます。自分を信じ、諦めずに前進し続けてください。困難に直面しても、愛の精神を忘れなければ、必ず乗り越えられます。あなたには無限の可能性があります。'),
(1490, 149007, 14900701, '【MAMALAHOE】のカードが出ました。MAMALAHOEは同情を象徴し、自分の気持ちを最優先することを教えてくれます。理想の相手を左右する要因について、同情という視点から捉えることが非常に重要です。同情のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149007, 14900702, '【PUA】のカードが出ました。カードのテーマは「目的を定め前進あるのみ」です。理想の相手を左右する要因について、前進という視点から捉えることが非常に重要です。前進のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149007, 14900703, '出たカードは【NALU】。感情を観察することを示す重要なカードです。理想の相手を左右する要因について、黙想という視点から捉えることが非常に重要です。黙想のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149007, 14900704, '出たカードは【POHAKU】。人間関係を見直すことを示す重要なカードです。理想の相手を左右する要因について、関係という視点から捉えることが非常に重要です。関係のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149008, 14900801, '出たカードは【HI’IAKA】。望む結果をイメージすることを示す重要なカードです。改善のための魅力の高め方について、献身という視点から捉えることが非常に重要です。献身のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149008, 14900802, '【PELE】のカードが出ました。PELEは隆起を象徴し、情熱を注ぐことを教えてくれます。改善のための魅力の高め方について、隆起という視点から捉えることが非常に重要です。隆起のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149008, 14900803, '【HO’OPONOPONO】のカードが出ました。カードのテーマは「コミュニケーションを大切にする」です。改善のための魅力の高め方について、コミュニケーションという視点から捉えることが非常に重要です。コミュニケーションのエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149008, 14900804, '出たカードは【LAMAKU】。意思の力で喜びを手にすることを示す重要なカードです。改善のための魅力の高め方について、勝利という視点から捉えることが非常に重要です。勝利のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149009, 14900901, '【I’O】のカードが出ました。このカードは「全ては必然で起こっている」を意味します。恋愛ブロックの深層について、ミステリーという視点から捉えることが非常に重要です。ミステリーのエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149009, 14900902, '【TAPA】のカードが示されました。「パートナーシップ、距離感を考える」というメッセージが込められています。恋愛ブロックの深層について、結合という視点から捉えることが非常に重要です。結合のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149009, 14900903, '出たカードは【PUA】。目的を定め前進あるのみことを示す重要なカードです。恋愛ブロックの深層について、前進という視点から捉えることが非常に重要です。前進のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。'),
(1490, 149009, 14900904, '出たカードは【ANO’ANO】。思考が現実を創る／全てを受けとめることを示す重要なカードです。恋愛ブロックの深層について、意思という視点から捉えることが非常に重要です。意思のエネルギーを意識し、それに沿って考え、行動することで、新たな気づきと理解が得られるはずです。物事の表面だけでなく、深層にある真実を見極める目を養うことが大切です。直感を信じ、心の声に耳を傾けながら、一歩ずつ前進してください。答えは必ず見つかります。');
