INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1527, 152701, '心地よさが教えてくれること'),
(1527, 152702, '心身を委ねる人の視点から見た調和'),
(1527, 152703, 'あの方の洗練に対する想い'),
(1527, 152704, '完璧における信頼する人の役割'),
(1527, 152705, '到達を導く意識すること'),
