INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1529, '今の段階の全貌　真の満足詳細占い', '現時点の状態が見える', '人生の意義と今の段階について。本当のあなたに関する使命を、ハワイアンタロットが徹底解明。', 7, NULL, NULL, NULL, NULL, NULL, '2027-05-03 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1529, 152901, '本当の姿の心の充足と現在のフェーズ'),
(1529, 152902, '内なる喜びの本質'),
(1529, 152903, '向かうべき方向がもたらす今の段階'),
(1529, 152904, '深層の自己が感じていること'),
(1529, 152905, '現在の状況の可能性'),
(1529, 152906, '真の自分にとっての目指す方向性'),
(1529, 152907, '現在の位置を導く取るべき方向'),
