INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1532, '縁の強さの意味　幸せを求めるあなたが迎える出会いの成就', '運命の人の核心', '運命を信じるあなたの恋愛適性、そして恋の開始…　ハワイアンタロットが理想の相手の全てをお伝えします。', 10, NULL, NULL, NULL, NULL, NULL, '2027-05-06 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1532, 153201, '出会いの成就への恋の運命'),
(1532, 153202, '愛を探すあなたにとっての出会うための行動'),
(1532, 153203, '新しい恋を実現するオープンマインド'),
(1532, 153204, '運命の瞬間を実現する心がけること'),
(1532, 153205, '出会いの予兆における重要ポイント');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1532, 153201, 15320101, '【POHAKU】のカードが示されました。「人間関係を見直す」というメッセージが込められています。出会いの成就への恋の運命に対するハワイアンタロットの答えは明確です。それは関係です。この関係という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。関係は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で関係を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1532, 153201, 15320102, '【WA’A】のカードが語りかけます。大きな変化が訪れるという智恵を、あなたに授けようとしています。カードが示す出会いの成就への恋の運命の答えは、移動にあります。この移動を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、移動という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、移動の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1532, 153201, 15320103, '【PELE】のカードが出ました。このカードは「情熱を注ぐ」を意味します。出会いの成就への恋の運命を考える上で、隆起は欠かせない要素となっています。隆起の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、隆起の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、隆起のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1532, 153201, 15320104, '【I’O】のカードが出ました。このカードは「全ては必然で起こっている」を意味します。カードが明かす出会いの成就への恋の運命の真実において、ミステリーは中心的な役割を果たしています。ミステリーを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。ミステリーのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、ミステリーという贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1532, 153202, 15320201, '【KU/HINA】が選ばれました。「活動する/休息する」がこのカードの核心的メッセージです。あなたが知りたい愛を探すあなたにとっての出会うための行動の核心は、戦い/癒しにあります。戦い/癒しというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。戦い/癒しを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1532, 153202, 15320202, '【ALOHA】のカードが出ました。このカードは「真の愛を学ぶ」を意味します。あなたが知りたい愛を探すあなたにとっての出会うための行動の核心は、愛にあります。愛というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。愛を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1532, 153202, 15320203, 'ハワイアンタロットの【ANO’ANO】。意思のエネルギーを持ち、思考が現実を創る／全てを受けとめることを説いています。あなたの愛を探すあなたにとっての出会うための行動には、意思が深く関わっています。この意思というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。意思のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、意思と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1532, 153202, 15320204, '【MANO】のカードです。MANOが示すのはリーダーシップであり、リーダーシップを取ることの重要性です。カードが示す愛を探すあなたにとっての出会うための行動の答えは、リーダーシップにあります。このリーダーシップを軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、リーダーシップという羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、リーダーシップの導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1532, 153203, 15320301, 'ハワイアンタロットの【LONO/KAMAPUA’A】。収穫/豊穣のエネルギーを持ち、やり切ることで収穫に繋がることを説いています。新しい恋を実現するオープンマインドに対するハワイアンタロットの答えは明確です。それは収穫/豊穣です。この収穫/豊穣という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。収穫/豊穣は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で収穫/豊穣を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1532, 153203, 15320302, '【IWI】のカードです。IWIが示すのは尊敬であり、自分も他人も大切にすることの重要性です。新しい恋を実現するオープンマインドに対するハワイアンタロットの答えは明確です。それは尊敬です。この尊敬という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。尊敬は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で尊敬を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1532, 153203, 15320303, '出たカードは【PAHU】。ターニングポイントことを示す重要なカードです。カードが示す新しい恋を実現するオープンマインドの答えは、タイミング・儀式にあります。このタイミング・儀式を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、タイミング・儀式という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、タイミング・儀式の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1532, 153203, 15320304, '【HINA】のカードが語りかけます。自分自身を成長させるという智恵を、あなたに授けようとしています。タロットが告げる新しい恋を実現するオープンマインドに関する真実は、養いに集約されています。養いという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。養いを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1532, 153204, 15320401, '出たカードは【NAUPAKA】。切り離すことを示す重要なカードです。カードが示す運命の瞬間を実現する心がけることの答えは、別離にあります。この別離を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、別離という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、別離の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1532, 153204, 15320402, 'タロットが告げるのは【PUHI】。崩壊というキーワードで、新しい創造のための破壊ことを伝えています。運命の瞬間を実現する心がけることを考える上で、崩壊は欠かせない要素となっています。崩壊の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、崩壊の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、崩壊のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1532, 153204, 15320403, '【MENEHUNE】が選ばれました。「コツコツと積み重ねる」がこのカードの核心的メッセージです。運命の瞬間を実現する心がけることにおいて最も大切なのは、持続という概念を正しく認識することです。持続は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて持続と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、持続と共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1532, 153204, 15320404, 'タロットが告げるのは【PU’OLO】。解放というキーワードで、不必要なものを手放すことを伝えています。タロットが告げる運命の瞬間を実現する心がけることに関する真実は、解放に集約されています。解放という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。解放を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1532, 153205, 15320501, 'ハワイアンタロットの【ANUENUE】。変容のエネルギーを持ち、隠れた部分に光をあてることを説いています。あなたが知りたい出会いの予兆における重要ポイントの核心は、変容にあります。変容というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。変容を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1532, 153205, 15320502, '【LAMAKU】のカードが現れました。勝利を象徴し、意思の力で喜びを手にすることを教えてくれます。あなたの出会いの予兆における重要ポイントには、勝利が深く関わっています。この勝利というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。勝利のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、勝利と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1532, 153205, 15320503, '【PAPA&WAKEA】のカードが出ました。このカードは「葛藤の中から新しいものを生み出す」を意味します。カードが明かす出会いの予兆における重要ポイントの真実において、苦闘・全体は中心的な役割を果たしています。苦闘・全体を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。苦闘・全体のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、苦闘・全体という贈り物を大切に受け取ってください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1532, 153205, 15320504, '【MO’O】のカードです。MO’Oが示すのは罠であり、不安が成功を妨げることの重要性です。タロットが告げる出会いの予兆における重要ポイントに関する真実は、罠に集約されています。罠という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。罠を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。');
