INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1534, '想いの到達の方向性　心奪われた人の想いの帰結は', '真実が明らかに', '想いの帰結への道筋が見えてくる。素の気持ちを軸にした愛の開花を、ハワイアンタロットで鑑定。', 2, NULL, NULL, NULL, NULL, NULL, '2027-05-08 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1534, 153401, '気になる人が求める愛の獲得'),
(1534, 153402, '想いの行方の展望'),
(1534, 153403, '恋の成り行きへの真の感情'),
(1534, 153404, '隠れた感情の深層'),
(1534, 153405, '真の感情における愛の実り'),
(1534, 153406, '想いの終点の展望'),
(1534, 153407, '想いの帰結における恋の実現'),
