INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1535, '気持ちの真実を解く鍵　先々の状況へのヒント', '将来の姿が見える', '深層の心が気になる…　未来の形はどうなる？　ハワイアンタロットがあの人の真実を明らかにします。', 3, NULL, NULL, NULL, NULL, NULL, '2027-05-09 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1535, 153501, '知りたい相手が大切にすること'),
(1535, 153502, '先の動きを導く正しい対応'),
(1535, 153503, '将来像への道筋'),
(1535, 153504, '感情の本体の真相'),
(1535, 153505, '今後の展開を導く正解の道'),
(1535, 153506, '相手が感じていること'),
(1535, 153507, '今後の見込みを導く推奨される行動');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1535, 153501, 15350101, '【KU/HINA】のカードが出ました。このカードは「活動する/休息する」を意味します。あなたが知りたい知りたい相手が大切にすることの核心は、戦い/癒しにあります。戦い/癒しというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。戦い/癒しを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153501, 15350102, '【MO’O】のカードが現れました。罠を象徴し、不安が成功を妨げることを教えてくれます。あなたが知りたい知りたい相手が大切にすることの核心は、罠にあります。罠というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。罠を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153501, 15350103, '【PU’OLO】のカードが現れました。解放を象徴し、不必要なものを手放すことを教えてくれます。あなたが知りたい知りたい相手が大切にすることの核心は、解放にあります。解放というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。解放を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153501, 15350104, '【TI】のカードです。TIが示すのは浄化であり、感情、肉体、精神の浄化をすることの重要性です。あなたが知りたい知りたい相手が大切にすることの核心は、浄化にあります。浄化というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。浄化を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153502, 15350201, '【PUHI】のカードが現れました。崩壊を象徴し、新しい創造のための破壊ことを教えてくれます。先の動きを導く正しい対応において、崩壊が極めて重要な役割を果たしています。この崩壊のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。崩壊という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153502, 15350202, '【PAHU】のカードです。PAHUが示すのはタイミング・儀式であり、ターニングポイントことの重要性です。あなたの先の動きを導く正しい対応には、タイミング・儀式が深く関わっています。このタイミング・儀式というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。タイミング・儀式のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、タイミング・儀式と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1535, 153502, 15350203, '出たカードは【PUA】。目的を定め前進あるのみことを示す重要なカードです。先の動きを導く正しい対応に関して、カードは前進という重要なメッセージを伝えています。あなたが今必要としているのは、この前進を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。前進の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153502, 15350204, '【MANO】のカードが出ました。このカードは「リーダーシップを取る」を意味します。カードが明かす先の動きを導く正しい対応の真実において、リーダーシップは中心的な役割を果たしています。リーダーシップを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。リーダーシップのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、リーダーシップという贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153503, 15350301, 'ハワイアンタロットの【LA’AMAOMAO】。償いのエネルギーを持ち、失敗は成功の学びことを説いています。あなたの将来像への道筋には、償いが深く関わっています。この償いというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。償いのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、償いと共に歩んでいきましょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153503, 15350302, '【KANE】が選ばれました。「もう一度始める」がこのカードの核心的メッセージです。あなたの将来像への道筋には、創造が深く関わっています。この創造というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。創造のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、創造と共に歩んでいきましょう。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153503, 15350303, '出たカードは【HI’IAKA】。望む結果をイメージすることを示す重要なカードです。あなたの将来像への道筋には、献身が深く関わっています。この献身というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。献身のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、献身と共に歩んでいきましょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1535, 153503, 15350304, 'タロットが告げるのは【KA WAI A KA PILILIKO】。反映というキーワードで、ありのままを受け入れることを伝えています。カードが示す将来像への道筋の答えは、反映にあります。この反映を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、反映という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、反映の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1535, 153504, 15350401, '出たカードは【ANO’ANO】。思考が現実を創る／全てを受けとめることを示す重要なカードです。カードが明かす感情の本体の真相の真実において、意思は中心的な役割を果たしています。意思を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。意思のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、意思という贈り物を大切に受け取ってください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153504, 15350402, 'カードに現れたのは【PUEO】。テーマは「自然の流れに身をまかせる」です。感情の本体の真相において最も大切なのは、導きという概念を正しく認識することです。導きは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて導きと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、導きと共に成長していきましょう。未来は明るく輝いています。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153504, 15350403, '【NALU】が選ばれました。「感情を観察する」がこのカードの核心的メッセージです。感情の本体の真相を考える上で、黙想は欠かせない要素となっています。黙想の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、黙想の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、黙想のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1535, 153504, 15350404, 'カードに現れたのは【NAUPAKA】。テーマは「切り離す」です。感情の本体の真相に対するハワイアンタロットの答えは明確です。それは別離です。この別離という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。別離は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で別離を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153505, 15350501, 'カードに現れたのは【TAPA】。テーマは「パートナーシップ、距離感を考える」です。あなたが知りたい今後の展開を導く正解の道の核心は、結合にあります。結合というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。結合を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153505, 15350502, 'カードに現れたのは【LEI】。テーマは「日々の暮らしを大切に」です。今後の展開を導く正解の道に対するハワイアンタロットの答えは明確です。それは大切です。この大切という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。大切は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で大切を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1535, 153505, 15350503, '【HAUMEA】のカードです。HAUMEAが示すのは滋養であり、必要なものは与えられることの重要性です。今後の展開を導く正解の道を考える上で、滋養は欠かせない要素となっています。滋養の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、滋養の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、滋養のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153505, 15350504, '【PAPA&WAKEA】のカードが示されました。「葛藤の中から新しいものを生み出す」というメッセージが込められています。タロットが告げる今後の展開を導く正解の道に関する真実は、苦闘・全体に集約されています。苦闘・全体という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。苦闘・全体を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153506, 15350601, '【HULU】が選ばれました。「無限の可能性」がこのカードの核心的メッセージです。相手が感じていることにおいて最も大切なのは、イリュージョンという概念を正しく認識することです。イリュージョンは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイリュージョンと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イリュージョンと共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153506, 15350602, '【ANUENUE】のカードが語りかけます。隠れた部分に光をあてるという智恵を、あなたに授けようとしています。相手が感じていることにおいて最も大切なのは、変容という概念を正しく認識することです。変容は、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけて変容と向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、変容と共に成長していきましょう。未来は明るく輝いています。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153506, 15350603, '【LAMAKU】のカードが出ました。このカードは「意思の力で喜びを手にする」を意味します。相手が感じていることを考える上で、勝利は欠かせない要素となっています。勝利の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、勝利の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、勝利のメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153506, 15350604, 'カードに現れたのは【HUNA】。テーマは「可能性を禁じない」です。相手が感じていることにおいて、秘密が極めて重要な役割を果たしています。この秘密のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。秘密という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1535, 153507, 15350701, '【LONO/KAMAPUA’A】のカードが示されました。「やり切ることで収穫に繋がる」というメッセージが込められています。あなたの今後の見込みを導く推奨される行動には、収穫/豊穣が深く関わっています。この収穫/豊穣というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。収穫/豊穣のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、収穫/豊穣と共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1535, 153507, 15350702, '【PO/AO】のカードが出ました。このカードは「深層心理を探る」を意味します。あなたが知りたい今後の見込みを導く推奨される行動の核心は、無意識/意識にあります。無意識/意識というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。無意識/意識を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1535, 153507, 15350703, '【ALOHA】のカードが示されました。「真の愛を学ぶ」というメッセージが込められています。今後の見込みを導く推奨される行動に対するハワイアンタロットの答えは明確です。それは愛です。この愛という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。愛は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で愛を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1535, 153507, 15350704, '【POLI’AHU】のカードが出ました。このカードは「不動の静けさからくる強さ」を意味します。タロットが告げる今後の見込みを導く推奨される行動に関する真実は、静けさの愛撫に集約されています。静けさの愛撫という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。静けさの愛撫を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。');
