INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1537, 153701, '豊かにすること'),
(1537, 153702, 'フィーリングが教えてくれること'),
(1537, 153703, '魂の伴侶の共鳴度と洗練'),
(1537, 153704, '充実させることがもたらす洗練'),
