INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1542, '出会いのタイミングへの道　特別な縁完全ガイド', '運命を読み解く', '素敵な縁を見据えて。モテ運から読み解く運命的な出会いを、ハワイアンタロットが詳しくお伝えします。', 10, NULL, NULL, NULL, NULL, NULL, '2027-05-16 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1542, 154201, '出会いを待つあなたにとっての広げること'),
(1542, 154202, '出会いの運命の真相'),
(1542, 154203, '魅力の輝きにおける新たな縁'),
(1542, 154204, '運命の出会いの核心'),
(1542, 154205, '恋愛の予感における重要ポイント'),
