INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1549, '生き方完全鑑定　原初の自分との現在の状況', '真実が明らかに', 'あなた自身の真の満足、そして今の段階…　ハワイアンタロットが人生の軌跡の全てをお伝えします。', 7, NULL, NULL, NULL, NULL, NULL, '2027-05-23 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1549, 154901, '本当の姿にとっての辿るべき道'),
(1549, 154902, '魂の視点から見た心の充足'),
(1549, 154903, '自分が求める生きる意味'),
(1549, 154904, '真の幸せにおける運命の道'),
(1549, 154905, '究極の幸せから見える真実');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1549, 154901, 15490101, '【HO’OPONOPONO】のカードが語りかけます。コミュニケーションを大切にするという智恵を、あなたに授けようとしています。本当の姿にとっての辿るべき道を考える上で、コミュニケーションは欠かせない要素となっています。コミュニケーションの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、コミュニケーションの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、コミュニケーションのメッセージを受け取りましょう。道は必ず開けます。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1549, 154901, 15490102, '【AWA】が選ばれました。「無条件と見返りの違いを意識する」がこのカードの核心的メッセージです。カードが示す本当の姿にとっての辿るべき道の答えは、供物・生贄にあります。この供物・生贄を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、供物・生贄という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、供物・生贄の導きに従ってください。答えは必ずそこにあります。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1549, 154901, 15490103, '【IWI】のカードが出ました。このカードは「自分も他人も大切にする」を意味します。本当の姿にとっての辿るべき道において、尊敬が極めて重要な役割を果たしています。この尊敬のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。尊敬という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1549, 154901, 15490104, '出たカードは【PUEO】。自然の流れに身をまかせることを示す重要なカードです。カードが明かす本当の姿にとっての辿るべき道の真実において、導きは中心的な役割を果たしています。導きを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。導きのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、導きという贈り物を大切に受け取ってください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1549, 154902, 15490201, '【LEHUA】のカードが語りかけます。安心して前進するという智恵を、あなたに授けようとしています。魂の視点から見た心の充足を考える上で、イニシエーションは欠かせない要素となっています。イニシエーションの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、イニシエーションの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、イニシエーションのメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154902, 15490202, '【LA’AMAOMAO】のカードが現れました。償いを象徴し、失敗は成功の学びことを教えてくれます。魂の視点から見た心の充足に関して、カードは償いという重要なメッセージを伝えています。あなたが今必要としているのは、この償いを深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。償いの真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154902, 15490203, 'タロットが告げるのは【HINA】。養いというキーワードで、自分自身を成長させることを伝えています。カードが明かす魂の視点から見た心の充足の真実において、養いは中心的な役割を果たしています。養いを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。養いのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、養いという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154902, 15490204, 'カードに現れたのは【TI】。テーマは「感情、肉体、精神の浄化をする」です。魂の視点から見た心の充足において、浄化が極めて重要な役割を果たしています。この浄化のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。浄化という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154903, 15490301, '【NAUPAKA】のカードが示されました。「切り離す」というメッセージが込められています。タロットが告げる自分が求める生きる意味に関する真実は、別離に集約されています。別離という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。別離を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1549, 154903, 15490302, 'カードに現れたのは【MENEHUNE】。テーマは「コツコツと積み重ねる」です。自分が求める生きる意味において、持続が極めて重要な役割を果たしています。この持続のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。持続という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1549, 154903, 15490303, '【HAUMEA】のカードが語りかけます。必要なものは与えられるという智恵を、あなたに授けようとしています。あなたが知りたい自分が求める生きる意味の核心は、滋養にあります。滋養というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。滋養を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154903, 15490304, '【MANA】のカードが現れました。力を与えることを象徴し、満ちているエネルギーをどう使うかことを教えてくれます。あなたの自分が求める生きる意味には、力を与えることが深く関わっています。この力を与えることというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。力を与えることのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、力を与えることと共に歩んでいきましょう。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154904, 15490401, 'タロットが告げるのは【MAMALAHOE】。同情というキーワードで、自分の気持ちを最優先することを伝えています。真の幸せにおける運命の道に関して、カードは同情という重要なメッセージを伝えています。あなたが今必要としているのは、この同情を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。同情の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1549, 154904, 15490402, 'ハワイアンタロットの【OLE】。物語のエネルギーを持ち、言葉を大切にすることを説いています。真の幸せにおける運命の道において、物語が極めて重要な役割を果たしています。この物語のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。物語という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154904, 15490403, '【POLI’AHU】のカードが示されました。「不動の静けさからくる強さ」というメッセージが込められています。真の幸せにおける運命の道という人生の重要なテーマにおいて、静けさの愛撫は羅針盤となります。人生の岐路に立ったとき、静けさの愛撫を指針とすることで、正しい選択ができるでしょう。静けさの愛撫は、あなたの使命や天命とも深く関わっています。この静けさの愛撫を追求し、極めることで、あなたの人生は本来あるべき姿へと近づいていきます。運命を信じ、静けさの愛撫に従って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1549, 154904, 15490404, '【I’O】のカードが出ました。このカードは「全ては必然で起こっている」を意味します。カードが明かす真の幸せにおける運命の道の真実において、ミステリーは中心的な役割を果たしています。ミステリーを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。ミステリーのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、ミステリーという贈り物を大切に受け取ってください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154905, 15490501, '【PELE】のカードが現れました。隆起を象徴し、情熱を注ぐことを教えてくれます。あなたの究極の幸せから見える真実には、隆起が深く関わっています。この隆起というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。隆起のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、隆起と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1549, 154905, 15490502, 'カードに現れたのは【LEI】。テーマは「日々の暮らしを大切に」です。あなたが知りたい究極の幸せから見える真実の核心は、大切にあります。大切というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。大切を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154905, 15490503, '出たカードは【HI’IAKA】。望む結果をイメージすることを示す重要なカードです。究極の幸せから見える真実に関して、カードは献身という重要なメッセージを伝えています。あなたが今必要としているのは、この献身を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。献身の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1549, 154905, 15490504, '出たカードは【KU/HINA】。活動する/休息することを示す重要なカードです。あなたの究極の幸せから見える真実には、戦い/癒しが深く関わっています。この戦い/癒しというテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。戦い/癒しのエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、戦い/癒しと共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
