INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1553, '永続する愛全公開　将来の関係への完全マップ', '正直な心詳細', '心の奥と進む道について。大切な人に関する愛の確認を、ハワイアンタロットが徹底解明。', 1, NULL, NULL, NULL, NULL, NULL, '2027-05-27 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1553, 155301, '関係の進展を左右する要因'),
(1553, 155302, '秘めた思いが示すもの'),
(1553, 155303, 'かけがえのない人の視点から見た心の奥'),
(1553, 155304, '心の声の本質'),
(1553, 155305, '運命の人の真の気持ち'),
(1553, 155306, '迎える明日への道筋'),
(1553, 155307, '真の気持ちが教えてくれること');
INSERT INTO flowt_seimei.mana_result (contents_id, menu_id, result_id, body) VALUES
(1553, 155301, 15530101, '【OLE】のカードが示されました。「言葉を大切にする」というメッセージが込められています。関係の進展を左右する要因を考える上で、物語は欠かせない要素となっています。物語の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、物語の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、物語のメッセージを受け取りましょう。道は必ず開けます。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155301, 15530102, '【LAKA】のカードです。LAKAが示すのはインスピレーションであり、直感を大切にすることの重要性です。関係の進展を左右する要因に対するハワイアンタロットの答えは明確です。それはインスピレーションです。このインスピレーションという要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。インスピレーションは、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中でインスピレーションを意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155301, 15530103, '【KA WAI A KA PILILIKO】のカードが現れました。反映を象徴し、ありのままを受け入れることを教えてくれます。関係の進展を左右する要因において、反映が極めて重要な役割を果たしています。この反映のエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。反映という視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155301, 15530104, '【LA’AMAOMAO】のカードが示されました。「失敗は成功の学び」というメッセージが込められています。関係の進展を左右する要因を考える上で、償いは欠かせない要素となっています。償いの本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、償いの視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、償いのメッセージを受け取りましょう。道は必ず開けます。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1553, 155302, 15530201, '【TI】のカードが語りかけます。感情、肉体、精神の浄化をするという智恵を、あなたに授けようとしています。秘めた思いが示すものに秘められた真実は、浄化によって明らかになります。愛に関わるあらゆる疑問や不安は、浄化という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、浄化を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。浄化さえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155302, 15530202, 'ハワイアンタロットの【LEHUA】。イニシエーションのエネルギーを持ち、安心して前進することを説いています。秘めた思いが示すものにおいて最も大切なのは、イニシエーションという概念を正しく認識することです。イニシエーションは、あなたの現状を打破し、新たなステージへと導く力を持っています。今はまだピンとこないかもしれませんが、時間をかけてイニシエーションと向き合うことで、その深い意味が理解できるようになります。急がず、焦らず、イニシエーションと共に成長していきましょう。未来は明るく輝いています。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155302, 15530203, '出たカードは【LEI】。日々の暮らしを大切にことを示す重要なカードです。カードが示す秘めた思いが示すものの答えは、大切にあります。この大切を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、大切という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、大切の導きに従ってください。答えは必ずそこにあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155302, 15530204, '出たカードは【HINA】。自分自身を成長させることを示す重要なカードです。秘めた思いが示すものにおいて、養いが極めて重要な役割を果たしています。この養いのエネルギーを正しく理解し、日々の中で意識することで、あなたの望む方向へと確実に進んでいけるでしょう。時には思いもよらない展開があるかもしれませんが、それもすべて成長のための貴重な経験です。養いという視点を持ち続けることで、真実が明らかになり、道が開けていきます。焦らず、一歩ずつ着実に前進してください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155303, 15530301, '出たカードは【LAMAKU】。意思の力で喜びを手にすることを示す重要なカードです。タロットが告げるかけがえのない人の視点から見た心の奥に関する真実は、勝利に集約されています。勝利という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。勝利を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155303, 15530302, '出たカードは【ANO’ANO】。思考が現実を創る／全てを受けとめることを示す重要なカードです。恋愛におけるかけがえのない人の視点から見た心の奥を占う上で、意思以上に重要なものはありません。意思は、二人の関係を支える基盤であり、愛を育む土壌です。この意思を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、意思があれば必ず乗り越えられます。愛を信じ、意思と共に歩んでください。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155303, 15530303, '出たカードは【PUA】。目的を定め前進あるのみことを示す重要なカードです。かけがえのない人の視点から見た心の奥に対するハワイアンタロットの答えは明確です。それは前進です。この前進という要素を、あなたの人生にどう取り入れるかが、今後の展開を大きく左右します。前進は、単なる概念ではなく、実践すべき具体的な指針です。日々の選択や行動の中で前進を意識し、それに沿って生きることで、人生は豊かに実り多いものとなるでしょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155303, 15530304, '【HULU】のカードが示されました。「無限の可能性」というメッセージが込められています。かけがえのない人の視点から見た心の奥に秘められた真実は、イリュージョンによって明らかになります。愛に関わるあらゆる疑問や不安は、イリュージョンという視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、イリュージョンを中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。イリュージョンさえ忘れなければ、二人の未来は明るく輝くでしょう。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155304, 15530401, '【PU’OLO】のカードが出ました。このカードは「不必要なものを手放す」を意味します。あなたの心の声の本質には、解放が深く関わっています。この解放というテーマを無視することはできません。むしろ、それを受け入れ、自分のものとすることで、新たな可能性が開けてきます。解放のエネルギーは強力で、あなたを望む未来へと導く力を持っています。ただし、それには素直な心と前向きな姿勢が必要です。恐れずに、解放と共に歩んでいきましょう。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155304, 15530402, 'タロットが告げるのは【PAPA&WAKEA】。苦闘・全体というキーワードで、葛藤の中から新しいものを生み出すことを伝えています。恋愛における心の声の本質を占う上で、苦闘・全体以上に重要なものはありません。苦闘・全体は、二人の関係を支える基盤であり、愛を育む土壌です。この苦闘・全体を大切にし、日々実践することで、関係は確実に深まり、絆は強固なものとなります。時には試練もありますが、苦闘・全体があれば必ず乗り越えられます。愛を信じ、苦闘・全体と共に歩んでください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1553, 155304, 15530403, 'タロットが告げるのは【KU/HINA】。戦い/癒しというキーワードで、活動する/休息することを伝えています。タロットが告げる心の声の本質に関する真実は、戦い/癒しに集約されています。戦い/癒しという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。戦い/癒しを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155304, 15530404, '【MAMALAHOE】が選ばれました。「自分の気持ちを最優先する」がこのカードの核心的メッセージです。心の声の本質に秘められた真実は、同情によって明らかになります。愛に関わるあらゆる疑問や不安は、同情という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、同情を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。同情さえ忘れなければ、二人の未来は明るく輝くでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155305, 15530501, 'タロットが告げるのは【LONO/KAMAPUA’A】。収穫/豊穣というキーワードで、やり切ることで収穫に繋がることを伝えています。運命の人の真の気持ちに関して、カードは収穫/豊穣という重要なメッセージを伝えています。あなたが今必要としているのは、この収穫/豊穣を深く理解し、それを実践に移していくことです。表面的な理解だけでは不十分で、本質的な部分まで掘り下げる必要があります。収穫/豊穣の真の意味を悟ることで、状況は大きく好転し、あなたの願いは実現に向かって動き出すでしょう。信じる心を持ち続けてください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155305, 15530502, 'カードに現れたのは【I’O】。テーマは「全ては必然で起こっている」です。カードが明かす運命の人の真の気持ちの真実において、ミステリーは中心的な役割を果たしています。ミステリーを理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。ミステリーのエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、ミステリーという贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1553, 155305, 15530503, 'ハワイアンタロットの【MENEHUNE】。持続のエネルギーを持ち、コツコツと積み重ねることを説いています。カードが示す運命の人の真の気持ちの答えは、持続にあります。この持続を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、持続という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、持続の導きに従ってください。答えは必ずそこにあります。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155305, 15530504, '【HAUMEA】のカードが現れました。滋養を象徴し、必要なものは与えられることを教えてくれます。運命の人の真の気持ちに秘められた真実は、滋養によって明らかになります。愛に関わるあらゆる疑問や不安は、滋養という視点から見直すことで解消されていきます。二人の絆を深め、関係をより良いものにするためには、滋養を中心に据えた行動が不可欠です。愛は複雑なようで実はシンプル。滋養さえ忘れなければ、二人の未来は明るく輝くでしょう。カードのメッセージを心に留め、前向きに歩んでいってください。'),
(1553, 155306, 15530601, '出たカードは【NALU】。感情を観察することを示す重要なカードです。カードが示す迎える明日への道筋の答えは、黙想にあります。この黙想を軸にして物事を考え、行動することで、あなたは正しい道を歩むことができるでしょう。今は混乱や不安があるかもしれませんが、黙想という羅針盤があれば、必ず目的地に辿り着けます。自分を信じ、黙想の導きに従ってください。答えは必ずそこにあります。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155306, 15530602, '【MANA】のカードが出ました。このカードは「満ちているエネルギーをどう使うか」を意味します。タロットが告げる迎える明日への道筋に関する真実は、力を与えることに集約されています。力を与えることという言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。力を与えることを深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1553, 155306, 15530603, '出たカードは【HUNA】。可能性を禁じないことを示す重要なカードです。カードが明かす迎える明日への道筋の真実において、秘密は中心的な役割を果たしています。秘密を理解することは、単に知識を得ることではなく、自己変容のプロセスでもあります。秘密のエネルギーを受け入れ、それと一体となることで、あなたは新しい自分に生まれ変わることができます。この機会を逃さず、秘密という贈り物を大切に受け取ってください。タロットの導きに従い、幸せな未来を自らの手で掴み取ってください。'),
(1553, 155306, 15530604, '【POLI’AHU】のカードが出ました。このカードは「不動の静けさからくる強さ」を意味します。迎える明日への道筋を考える上で、静けさの愛撫は欠かせない要素となっています。静けさの愛撫の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、静けさの愛撫の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、静けさの愛撫のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1553, 155307, 15530701, '【ALOHA】のカードが出ました。このカードは「真の愛を学ぶ」を意味します。真の気持ちが教えてくれることを考える上で、愛は欠かせない要素となっています。愛の本質を理解し、それに基づいて判断や選択を行うことが、成功への鍵です。一見困難に思える状況でも、愛の視点から見れば、新たな可能性や解決策が見えてくるはずです。柔軟な思考と開かれた心を持って、愛のメッセージを受け取りましょう。道は必ず開けます。カードが示す道を信じて、勇気を持って一歩を踏み出してください。'),
(1553, 155307, 15530702, 'カードに現れたのは【MANO】。テーマは「リーダーシップを取る」です。あなたが知りたい真の気持ちが教えてくれることの核心は、リーダーシップにあります。リーダーシップというキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。リーダーシップを胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。この啓示を大切にし、人生をより豊かなものにしていきましょう。'),
(1553, 155307, 15530703, '【KANALOA】が選ばれました。「基盤づくりをする」がこのカードの核心的メッセージです。あなたが知りたい真の気持ちが教えてくれることの核心は、基盤にあります。基盤というキーワードが、すべての疑問に対する答えを内包しています。このメッセージを受け取ったということは、あなたが正しい道を歩んでいる証拠です。基盤を胸に刻み、日々の生活の中で実践していくことで、望む未来が確実に近づいてきます。信念を持って進んでください。ハワイアンタロットの智恵を信じ、自分の道を進んでください。'),
(1553, 155307, 15530704, '【NAUPAKA】のカードが出ました。このカードは「切り離す」を意味します。タロットが告げる真の気持ちが教えてくれることに関する真実は、別離に集約されています。別離という言葉の中に、あなたが求めている答えのすべてが込められています。これは単なる偶然ではなく、宇宙からのメッセージです。別離を深く瞑想し、その意味を自分なりに解釈することで、真実が見えてきます。直感を信じ、心の声に耳を傾けてください。真実はあなたの中にあります。ハワイアンタロットの智恵を信じ、自分の道を進んでください。');
