INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1555, '偽りない感情が知りたい！　あの人との先の見通し', '例の人の真実', '先行きの予測を知りたい！　知りたい相手の深層の心は？　ハワイアンタロットが想いの真実を徹底鑑定します。', 3, NULL, NULL, NULL, NULL, NULL, '2027-05-29 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1555, 155501, '先の動きの展望'),
(1555, 155502, '注目の相手が求める感情の核心'),
(1555, 155503, '先々の状況への本音の部分'),
(1555, 155504, '素直な心を深く知る'),
(1555, 155505, '大切な人の視点から見た偽りない感情'),
(1555, 155506, '想いの深層としての薦められる方法'),
(1555, 155507, '先行きの予測を導く適当な処置'),
