INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1562, '特別な縁完全鑑定　幸せを求めるあなたとの素敵な縁', '縁の始まりの核心', '出会いの運の本質、運命的瞬間の真相。愛を信じる自分について、ハワイアンタロットが素敵な出会いを完全鑑定。', 10, NULL, NULL, NULL, NULL, NULL, '2027-06-05 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1562, 156201, '素敵な縁について'),
(1562, 156202, '愛を信じる自分の恋の始まりに対する想い'),
(1562, 156203, '理想との遭遇について'),
(1562, 156204, '恋愛適性の深層'),
(1562, 156205, '新しい出会いの行方'),
(1562, 156206, '幸せを求めるあなたの出会いのタイミングに対する想い'),
(1562, 156207, '縁の巡りを深く知る'),
