INSERT INTO flowt_seimei.mana_contents (contents_id, name, catch, caption, category, tag_1, tag_2, tag_3, tag_4, tag_5, start_date) VALUES
(1569, '根源的な存在の内なる喜びと今のステージ　徹底解明', '充実感詳細', '現状が気になるあなたへ。真の幸せを含む生きがいを、ハワイアンタロットが完全鑑定。', 7, NULL, NULL, NULL, NULL, NULL, '2027-06-12 00:00:00');
INSERT INTO flowt_seimei.mana_menu (contents_id, menu_id, name) VALUES
(1569, 156901, '心の平安が示すもの'),
(1569, 156902, '向かうべき方向'),
(1569, 156903, '心の平安の本質'),
(1569, 156904, '人生の指針'),
(1569, 156905, '今のステージへの真の満足'),
(1569, 156906, '生の充実の意味と今の境遇'),
